var Typewriter,TypewriterBuilder,assert;assert=require("assert");Typewriter=require("./typewriter");TypewriterBuilder=function(e){var t;t=new Typewriter;t.setTargetDomElement(e);return{withAccuracy:function(e){this.accuracy=e;t.setAccuracy(this.accuracy);return this},withMinimumSpeed:function(e){this.minimumSpeed=e;t.setMinimumSpeed(this.minimumSpeed);return this},withMaximumSpeed:function(e){this.maximumSpeed=e;t.setMaximumSpeed(this.maximumSpeed);return this},withKeyboardLayout:function(e){this.keyboardLayout=e;t.setKeyboardLayout(this.keyboardLayout);return this},build:function(){assert.ok(this.accuracy!=null,"Accuracy must be set");assert.ok(this.minimumSpeed!=null,"MinimumSpeed must be set");assert.ok(this.maximumSpeed!=null,"MaximumSpeed must be set");if(this.keyboardLayout==null){t.setKeyboardLayout(require("./defaultkeyboardlayout"))}return t}}};module.exports=TypewriterBuilder;