var assert,random;random=require(__dirname+"/../src/random");assert=require("assert");describe("random#integerInRange",function(){it("Should throw an exception when invalid arguments are specified",function(){assert.throws(function(){return random.integerInRange()},/The minimum must be specified/);assert.throws(function(){return random.integerInRange("0")},/Min must be a Number/);assert.throws(function(){return random.integerInRange(.1)},/Min must be an integer/);assert.throws(function(){return random.integerInRange(0)},/The maximum must be specified/);assert.throws(function(){return random.integerInRange(0,"1")},/Max must be a Number/);assert.throws(function(){return random.integerInRange(0,1.1)},/Max must be an integer/);return assert.throws(function(){return random.integerInRange(1,0)},/Min must be less than or equal to Max/)});return it("Should not throw any exceptions when arguments are valid",function(){return assert.doesNotThrow(function(){return random.integerInRange(0,1)})})});