var assert,integerInRange;assert=require("assert");integerInRange=function(e,t){assert.ok(e!=null,"The minimum must be specified");assert.strictEqual(typeof e,"number","Min must be a Number");assert.strictEqual(~~e,e,"Min must be an integer");assert.ok(t!=null,"The maximum must be specified");assert.strictEqual(typeof t,"number","Max must be a Number");assert.strictEqual(~~t,t,"Max must be an integer");assert.strictEqual(e<=t,true,"Min must be less than or equal to Max");if(e===t){return e}return Math.floor(Math.random()*(t-e+1))+e};module.exports.integerInRange=integerInRange;